mapper.select:
select tt.OBJECT_NAME as table_name from (select rownum rn,t.* from(
select OBJECT_NAME  from user_objects where object_type='TABLE'
and OBJECT_NAME in (select TABLE_NAME from all_tables where TABLESPACE_NAME =#{tableSchema,jdbcType=VARCHAR}) order by CREATED desc) t where rownum <= #{end} ) tt where tt.rn> #{start};

select count(*)  from user_objects where object_type='TABLE'
and OBJECT_NAME in (select TABLE_NAME from all_tables where TABLESPACE_NAME =#{tableSchema,jdbcType=VARCHAR})
;
